	<script>
		// Tempo
		var select_time = 5; // segundos

		// Carousel de knives
		var weapons = $(".weapons.owl-carousel");
		weapons.owlCarousel({ loop: true, nav: false, dots: false, items: 3, autoplay: false, autoplayTimeout: 3000, autoWidth:true, margin: 10, center: true, onInitialized: init });
		function init () {
			weapons.find(".owl-item").each(function () {
				$(this).addClass($(this).find("svg").attr("class"));
			});
		}

		// - Scroll desktop
		$(document).on('mousewheel', function (e) {
			var direction = (e.originalEvent.wheelDelta/120) > 0 ? 'up' : 'down';
		    if (direction == 'down') {
		        weapons.trigger('next.owl');
		    } else {
		        weapons.trigger('prev.owl');
		    }
		    e.preventDefault();
		});

		// - Scroll mobile
		var init_touch = 0;
		$(document).on('touchstart', function(e) {
			init_touch = e.changedTouches[0].clientX;
		});
		$(document).on('touchmove', function(e) {
			cur_touch = e.changedTouches[0].clientX;
			if(cur_touch > init_touch) {
				weapons.trigger('next.owl');
			} else {
				weapons.trigger('prev.owl');
			}
			init_touch = cur_touch;
		});

		// Timer
		var i = select_time;
		$("#pageLoader .counter .effect,#pageLoader .counter .text").text(i);
		setInterval(function () {
			if (i > 0) {
				i--;
				$("#pageLoader .counter .effect").text(i);
				$("#pageLoader .counter .effect").addClass("run");
				setTimeout(function () {
					$("#pageLoader .counter .effect").removeClass("run");
				}, 300);
				$("#pageLoader .counter .text").text(i);
			}
		}, 1000);

		setTimeout(function () {
			$(".owl-item:not(.center)").addClass("gone");
			$(".owl-item.center").addClass("chosen");
		}, select_time * 1000);

		setTimeout(function () {
			$("#pageLoader").addClass("cut-in-half");
			$("body").addClass("almost-ready");
		}, (select_time * 1000) + 400);

		setTimeout(function () {
			$("body").addClass("ready");
		}, (select_time * 1000) + 1000);

	</script>

</div>